INSERT INTO `locations` (`id`, `type`, `name`, `loc`, `description`) VALUES
(1, 'mob', 'Окраина города', 'Зион', 'Небольшой участок пустошей, обжитый поселенцами Зиона. Благодаря их стараниям, огромному количеству ила и сложной системе ирригации, связанной с рекой Ленточкой выжженая каменистая почва вновь стала плодоносить. Стараниями стражников из серого дозора, пораженные скверной существа здесь почти не нападают на мирное поселение и, пожалуй, это последний относительно спокойный участок суши.'),
(2, 'mob', 'Лес королей', 'Зион', 'В давние ремена, еще до Катаклизма, в этом лесу, охотились повелетили Старой Империи, правящие из Романьи, руины которой лежат в полудневном переходе от врат Зиона. Ныне же этот лес стал мрачен и зарос столь густо, что лучи солнца не проникают сквозь кроны покареженных древ. Из-за царящего здесь сумрака множество монстров расплодилось в некогда прекрасных зарослях и любого неподготовленного к встрече с ними путника ждет неминуемая смерть.'),
(3, 'mob', 'Зеленые топи', 'Зион', 'И в прежние времена болота не были безопасны, а теперь, когда их наполнило множество безобразных тварей, они стали в разы опасней - один неверный шаг и жадные лапы утянут вас в трясину, а пасти разорвут нежное человеческое мясцо на сотни мелких и не очень кусочков, после чего вы естественным образом переродитесь в часть местной почвы. Неплохая судьба для героя.\r\n\r\n');
